mapper.select:
select

from user_new_building_activity
where user_id = #{userId,jdbcType=INTEGER} and activity_id = #{activityId,jdbcType=INTEGER} and activity_building_id = #{activityBuildingId,jdbcType=INTEGER}

select id, user_id AS userId, user_phone AS userPhone, user_call_name AS userCallName, activity_building_id AS activityBuildingId, activity_building_name AS activityBuildingName, create_time AS createTime, city_id AS cityId, activity_id AS activityId from user_new_building_activity where 1=1
AND user_phone = #{userPhone}
AND activity_building_id = #{buildingId}
AND activity_building_name = #{buildingName}
AND activity_id = #{activityId}
AND user_call_name = #{userCallName}
