mapper.insert:
insert into user_favorite_condition (id, user_id, create_time, 
  update_time, is_del, condition,city_id,conditionType
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{isDel,jdbcType=INTEGER}, #{condition,jdbcType=OTHER},
  #{cityId,jdbcType=INTEGER},#{conditionType,jdbcType=INTEGER}
  )

mapper.select:
SELECT
  *
 FROM user_favorite_condition WHERE user_id = #{userId} and city_id = #{cityId} and is_del = 0 and condition_type=#{conditionType}

mapper.update:
UPDATE user_favorite_condition

    update_time = #{updateTime,jdbcType=TIMESTAMP},condition = '${condition}'




WHERE user_id = #{userId,jdbcType=INTEGER} and is_del = 0 and city_id = #{cityId,jdbcType=INTEGER} and condition_type=#{conditionType,jdbcType=INTEGER}

UPDATE user_favorite_condition

  is_del = 1

WHERE user_id = #{userId} and is_del = 0 and city_id = #{cityId} and condition_type = #{conditionType}
