mapper.select:
SELECT * FROM report_pipeline_record_everymonth
WHERE code = #{code,jdbcType=VARCHAR}
ORDER BY create_time DESC LIMIT 6 OFFSET 1

SELECT * FROM report_pipeline_record_everymonth
WHERE code = #{code, jdbcType=VARCHAR}
AND type = #{type, jdbcType=INTEGER}
ORDER BY create_time DESC LIMIT 1 OFFSET 1
